mapper.select:
select count(*) from( select wd.WORK_DETAIL_ID ,wd.WORK_SET_ID ,wd.WORK_DETAIL_NAME ,wd.WORK_ID ,wd.CREATE_DATE ,wd.EDIT_DATE ,w.WORK_NAME from pangwork.work_detail wd join pangwork.`work` w on w.WORK_ID = wd.WORK_ID where WORK_DETAIL_CATEGORY = 'B' AND w.USER_ID = #{params.userId}
AND w.WORK_ID = #{params.workId}
group by WORK_ID,DATE(wd.CREATE_DATE) order by wd.CREATE_DATE desc ) A

select wd.WORK_DETAIL_ID ,wd.WORK_SET_ID ,wd.WORK_DETAIL_NAME ,wd.WORK_ID ,wd.CREATE_DATE ,wd.EDIT_DATE ,w.WORK_NAME from pangwork.work_detail wd join pangwork.`work` w on w.WORK_ID = wd.WORK_ID where WORK_DETAIL_CATEGORY = 'B' AND w.USER_ID = #{params.userId}
AND w.WORK_ID = #{params.workId}
group by WORK_ID,DATE(wd.CREATE_DATE) order by wd.CREATE_DATE desc limit #{params.limit} offset #{params.offset}

SELECT D.* 
    FROM PANGWORK.WORK_DETAIL D
JOIN (
    SELECT WORK_ID, WORK_SET_ID
    FROM PANGWORK.WORK_DETAIL a
    WHERE WORK_DETAIL_ID = #{params.workDetailId}
    and a.WORK_ID =(
                    select WORK_ID
                    from PANGWORK.work b
                    where  b.WORK_ID =a.WORK_ID
                    and b.USER_ID =#{params.userId}
                )
) A
ON D.WORK_ID = A.WORK_ID
AND D.WORK_SET_ID = A.WORK_SET_ID;
